supplier = #{customerName}, contacts = #{contacts}, phonenum = #{phonenum}, email = #{email}, description = #{description}, advance_in = #{advanceIn}, begin_need_get = #{beginNeedGet}, begin_need_pay = #{beginNeedPay}, all_need_get = #{allNeedGet}, all_need_pay = #{allNeedPay}, fax = #{fax}, telephone = #{telephone}, tax_num = #{taxNum}, bank_name = #{bankName}, address = #{address}, account_number = #{accountNumber}, tax_rate = #{taxRate}
WHERE tenant_id = #{tenantId} AND id = #{id} AND delete_flag = 0

UPDATE erp_supplier

    enabled = #{enabled}

WHERE
    tenant_id = #{tenantId}
AND id = #{id}
AND delete_flag = 0

