mapper.update:
UPDATE erp_accounthead
type = #{type}, organ_id = #{organId}, hands_person_id = #{handsPersonId}, change_amount = #{changeAmount}, total_price = #{totalPrice}, account_id = #{accountId}, bill_time = #{billTime}, remark = #{remark}
WHERE id = #{id} AND tenant_id = #{userId}

UPDATE erp_accounthead

    delete_flag = #{deleteFlag}

WHERE
    id = #{id}
AND tenant_id = #{userId}

UPDATE erp_accountitem

    delete_flag = #{deleteFlag}

WHERE
    header_id = #{id}
AND tenant_id = #{userId}
