mapper.select:
SELECT b.id headerId, b.default_number defaultNumber, b.sub_type subType, CASE b.sub_type WHEN 1 THEN '采购入库' WHEN 2 THEN '销售退货' WHEN 3 THEN '零售退货' WHEN 4 THEN '其他入库' WHEN 5 THEN '销售出库' WHEN 6 THEN '采购退货' WHEN 7 THEN '调拨' WHEN 8 THEN '零售出库' WHEN 9 THEN '其他出库' ELSE '' END subTypeName, CONVERT(b.oper_time, CHAR) operTime, a.material_name materialName, a.material_model materialModel, a.oper_number operNumber, FORMAT(a.unit_price, 2) unitPrice, FORMAT(a.all_price, 2) allPrice, c.supplier supplierName, d.`name` depotName, b.status, b.status_content statusContent, b.link_number linkNumber FROM erp_depotitem a LEFT JOIN erp_depot d ON a.depot_id = d.id, erp_depothead b LEFT JOIN erp_supplier c ON b.organ_id = c.id WHERE a.tenant_id = #{userId} AND a.delete_flag = '0' AND a.header_id = b.id AND b.type = '2' AND b.sub_type != '10'
AND a.material_name LIKE '%${materialName}%'
AND a.depot_id = #{depotId}
AND b.organ_id = #{organId}
AND b.oper_time >= #{startTime} AND #{endTime} >= b.oper_time
ORDER BY b.oper_time DESC

SELECT b.id headerId, b.default_number defaultNumber, b.sub_type subType, CASE b.sub_type WHEN 1 THEN '采购入库' WHEN 2 THEN '销售退货' WHEN 3 THEN '零售退货' WHEN 4 THEN '其他入库' WHEN 5 THEN '销售出库' WHEN 6 THEN '采购退货' WHEN 7 THEN '调拨' WHEN 8 THEN '零售出库' WHEN 9 THEN '其他出库' ELSE '' END subTypeName, CONVERT(b.oper_time, CHAR) operTime, a.material_name materialName, a.material_model materialModel, a.oper_number operNumber, FORMAT(a.unit_price, 2) unitPrice, FORMAT(a.all_price, 2) allPrice, c.supplier supplierName, d.`name` depotName, b.status, b.status_content statusContent, b.link_number linkNumber FROM erp_depotitem a LEFT JOIN erp_depot d ON a.depot_id = d.id, erp_depothead b LEFT JOIN erp_supplier c ON b.organ_id = c.id WHERE a.tenant_id = #{userId} AND a.delete_flag = '0' AND a.header_id = b.id AND b.type = '1' AND b.sub_type != '11'
AND a.material_name LIKE '%${materialName}%'
AND a.depot_id = #{depotId}
AND b.organ_id = #{organId}
AND b.oper_time >= #{startTime} AND #{endTime} >= b.oper_time
ORDER BY b.oper_time DESC
