mapper.select:
SELECT b.id headerId, b.default_number defaultNumber, b.sub_type subType, CONVERT(b.oper_time, CHAR) operTime, a.material_name materialName, a.material_model materialModel, a.oper_number operNumber, FORMAT(a.unit_price, 2) unitPrice, FORMAT(a.all_price, 2) allPrice, c.supplier supplierName, d.`name` depotName FROM erp_depotitem a LEFT JOIN erp_depot d ON a.depot_id = d.id, erp_depothead b LEFT JOIN erp_supplier c ON b.organ_id = c.id WHERE a.tenant_id = #{userId} AND a.delete_flag = '0' AND a.header_id = b.id AND b.type = '2'
AND a.material_name LIKE '%${materialName}%'
AND a.depot_id = #{depotId}
AND b.organ_id = #{organId}
AND b.oper_time >= #{startTime} AND #{endTime} >= b.oper_time
ORDER BY b.oper_time DESC
