mapper.update:
UPDATE erp_depot
address = #{address}, warehousing = #{warehousing}, truckage = #{truckage}, remark = #{remark}, principal = #{principal}, is_default = #{isDefault}, name = #{name}
WHERE id = #{id} AND tenant_id = #{userId} AND delete_Flag = 0

UPDATE erp_depot

    delete_Flag = #{deleteFlag}

WHERE
    id = #{id}
AND tenant_id = #{userId}

UPDATE erp_depot

    is_default = #{isDefault}

WHERE
    id = #{id}
AND tenant_id = #{userId}
AND delete_Flag = 0

UPDATE erp_depot

    is_default = #{isDefault}

WHERE
    tenant_id = #{userId}
AND delete_Flag = 0

mapper.select:
SELECT
    d.id
FROM
    erp_depot d
WHERE
    d.id != #{id}
AND d.tenant_id = #{userId}
AND d.name = #{name}
AND d.delete_Flag = 0
ORDER BY d.create_time DESC LIMIT 0,1
